import { Component, Input, OnInit } from '@angular/core';
import { ApiService } from 'src/app/services/apiService';
import { Pedidos } from 'src/app/services/pedido.model';



@Component({
  selector: 'app-pedido',
  templateUrl: './pedido.page.html',
  styleUrls: ['./pedido.page.scss'],
})
export class PedidoPage implements OnInit {
tipoDiaria: string =''
periodo: string =''
private idPrestadorAtual!:number 




  constructor(private apiService:ApiService) {}
  tipoUserAtual:string=""
  perfilPrestador: any
  clientes: any

 
  ngOnInit() { 
    this.readPerfilPrestador();
    this.readClientes()
     
  }
  pedido: Pedidos = {
    prestador: {
       id : null
   },
   dataServico : '', 
   periodo : '',
   tipoDeDiaria : ''
  }

  onClick(){
    this.pedido.prestador.id = this.perfilPrestador.id
    this.pedido.dataServico = this.pedido.dataServico.split('/').reverse().join('-')
    console.log("Pedido Cadastrado!");
    console.log( "prestador Id = " + this.pedido.prestador.id)
    console.log( "data Serviço = " + this.pedido.dataServico)
    console.log( "periodo = " + this.pedido.periodo)
    console.log( "tipo Diaria = " + this.pedido.tipoDeDiaria)
    this.apiService.postPedido(this.pedido).subscribe(()=>{          })  

  }

  readPerfilPrestador(): void {
    this.apiService.readByIdPrestador().subscribe(
    (data) => {
       this.perfilPrestador = data;
      console.log('Dados dos prestadores pedido:', this.perfilPrestador);
   
     },
    (error) => {
       console.error('Erro ao obter dados dos prestadores:', error);
     }
    );
  }

  readClientes(): void {
    this.apiService.readByClientes().subscribe(
      (data) => {
        this.clientes = data;
        console.log('Dados dos clientes no pedido:', this.clientes);

      },
      (error) => {
        console.error('Erro ao obter dados dos clientes:', error);
      }
    );
  }

}
