import { Component, OnInit } from '@angular/core';
import { ApiService } from 'src/app/services/apiService';
import { prestadorFilter } from '../../services/prestadorFilter.model';
import { Usuarios } from 'src/app/services/usuarios.model';
import { Clientes } from 'src/app/services/clientes.model';



@Component({
  selector: 'app-hello-cliente',
  templateUrl: './hello-cliente.component.html',
  styleUrls: ['./hello-cliente.component.scss'],
})
export class HelloClienteComponent implements OnInit {

  constructor(
    private apiServiceByName: ApiService,
    private apiServiceByRegion: ApiService,
    private apiServiceClientes: ApiService
  ) { }

  nomeClienteLogado: string = '';
  nomePrestadorProcurado!: any

  prestadoresByName!: prestadorFilter[]
  prestadoresByRegion!: prestadorFilter[]
  usuarioLogado!:Usuarios[]
  clienteLogado!:Clientes[]
  mostraTodos!: boolean
  mostraName!: boolean
  mostraRegion!: boolean
  mostraTodosBotao!: boolean
  idCliente: number | undefined

  onMudouTermo(evento: any) {

    // Aciona addTermo() de apiService para salvar o valor do termo procurado
    this.apiServiceByName.addTermo(evento.novoTermo)

    // Aciona readByName() de ApiService para buscar resultados e guardar em prestadoresByName
    this.apiServiceByName.readByName().subscribe(prestadores => {
      this.prestadoresByName = prestadores;
      this.mostraTodos = false
      this.mostraName = true
      this.mostraRegion = false
      this.mostraTodosBotao = false
      console.log('Dados dos prestadores by Name:', this.prestadoresByName);
    })
  }

  onMudouRegion(evento: any) {

    // Aciona addRegion() de apiService para salvar o valor da região procurada
    this.apiServiceByRegion.addRegion(evento.novaRegiao)

    // Aciona readByRegion() de ApiService para buscar resultados e guardar em prestadoresByRegion
    this.apiServiceByRegion.readByRegion().subscribe(prestadores => {
      this.prestadoresByRegion = prestadores;
      this.mostraTodos = false
      this.mostraRegion = true
      this.mostraName = false
      this.mostraTodosBotao = false
      console.log('Dados dos prestadores by Region:', this.prestadoresByRegion);
    })
  }
  onClickTodos() {
    // No clique do botão mostrar todos 
    this.mostraTodos = false
    this.mostraRegion = false
    this.mostraName = false
    this.mostraTodosBotao = true
  }

  ngOnInit() {

    this.mostraTodos = true
    this.mostraName = false
    this.mostraRegion = false
    this.mostraTodosBotao = false
  }

}
